CREATE TABLE `qmsplan` (  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,  `title` varchar(200) NOT NULL,  `description` mediumtext,  `state` tinyint(3) unsigned DEFAULT '0',  `uid` char(10) NOT NULL,  `color` char(6) NOT NULL DEFAULT '000000',  `version` bigint(20) unsigned NOT NULL DEFAULT '0',  PRIMARY KEY (`id`)) ENGINE=InnoDB;
