INSERT IGNORE INTO `resinfotype` VALUES ('WOUND6','<tx tooltip=\"Wunden werden auf dem Überleitbogen auf Seite 2 Abschnitt 19 eingetragen. Ebenso wirken sich die Eintragunegn auf den Abschnitt 10 aus (Wundschmerz, Wunden)\"/>\r\n<label label=\"Du kannst zwar mehr als eine Stelle markieren, aber beschreibe nur *eine* Wunde pro Formular.\"  size=\"16\" fontstyle=\"bold\"/>\r\n<bodyscheme name=\"bs1\"/>\r\n\r\n<checkbox name=\"bedsore\" label=\"Diese Wunde ist ein Druckgeschwür (Dekubitus)\" tx=\"Wenn Du das hier markierst, wird das auch auf dem Überleitbogen auf Seite 1 Abschnitt 7 eingetragen.\" enables=\"epuap\" disables=\"daniel\" lockedforchanges=\"true\" default=\"true\"/>\r\n<combobox label=\"Kategorien nach EPUAP\" name=\"epuap\" tooltip=\"Denk dran, dass auch im weiteren Heilungs-Verlauf die anfängliche Wund-Kategorie [b]nicht mehr geändert[/b] wird. Sie bleibt bis zur Epithalisierung bestehen. Ein Dekubitus entwickelt sich also nicht rückwärts.\" lockedforchanges=\"true\">\r\n<item label=\"Kategorie I: Nicht wegdrückbare Rötung\" name=\"d1\" tooltip=\"Nicht wegdrückbare, umschriebene Rötung bei intakter Haut, gewöhnlich über einem knöchernen Vorsprung. Bei dunkel pigmentierter Haut ist ein Verblassen möglicherweise nicht sichtbar, die Farbe kann sich aber von der umgebenden Haut unterscheiden. Der Bereich kann schmerzempfindlich, verhärtet, weich, wärmer oder kälter sein als das umgebende Gewebe. Diese Symptome können auf eine (Dekubitus-) Gefährdung hinweisen.\"/>\r\n<item label=\"Kategorie II: Teilverlust der Haut\" name=\"d2\" tooltip=\"Teilzerstörung der Haut (bis zur Dermis), die als flaches, offenes Ulcus mit einem rot bis rosafarbenen Wundbett ohne Beläge in Erscheinung tritt. Kann sich auch als intakte oder offene/rupturierte, serumgefüllte Blase darstellen. Manifestiert sich als glänzendes oder trockenes, flaches Ulcus ohne nekrotisches Gewebe oder Bluterguss10. Diese Kategorie sollte nicht benutzt werden um Skin Tears (Gewebezerreißungen), Verbands- oder pflasterbedingte Hautschädigungen, feuchtigkeitsbedingte Läsionen, Mazerationen oder Abschürfungen zu beschreiben.\"/> \r\n<item label=\"Kategorie III: Verlust der Haut\" name=\"d3\" tooltip=\"Zerstörung aller Hautschichten. Subkutanes Fett kann sichtbar sein, jedoch keine Knochen, Muskeln oder Sehnen. Es kann ein Belag vorliegen, der jedoch nicht die Tiefe der Gewebsschädigung verschleiert. Es können Tunnel oder Unterminierungen vorliegen. Die Tiefe des Dekubitus der Kategorie III variiert je nach anatomischer Lokalisation. Der Nasenrücken, das Ohr, der Hinterkopf und das Gehörknöchelchen haben kein subkutanes Gewebe, daher können Kategorie III Wunden dort auch sehr oberflächlich sein. Im Gegensatz dazu können an besonders adipösen Körperstellen extrem tiefe Kategorie III Wunden auftreten. Knochen und Sehnen sind nicht sichtbar oder tastbar.\"/> \r\n<item label=\"Kategorie IV: vollständiger Haut oder Gewebeverlust\" name=\"d4\" tooltip=\"Totaler Gewebsverlust mit freiliegenden Knochen, Sehnen oder Muskeln. Belag und Schorf können vorliegen. Tunnel oder Unterminierungen liegen oft vor. Die Tiefe des Kategorie IV Dekubitus hängt von der anatomischen Lokalisation ab. Der Nasenrücken, das Ohr, der Hinterkopf und der Knochenvorsprung am Fußknöchel haben kein subkutanes Gewebe, daher können Wunden dort auch sehr oberflächlich sein. Kategorie IV Wunden können sich in Muskeln oder unterstützende Strukturen ausbreiten (Fascien, Sehnen oder Gelenkkapseln) und können dabei leicht Osteomyelitis oder Ostitis verursachen. Knochen und Sehnen sind sichtbar oder tastbar.\"/>\r\n</combobox>\r\n<combobox label=\"Wund-Stadium nach Daniel\" name=\"daniel\" defaultdisabled=\"true\">\r\n<item label=\"Wunde ist abgeheilt\" name=\"d0\"/>\r\n<item label=\"1. Grad Hautrötung\" name=\"d1\"/>\r\n<item label=\"2. Grad Blasenbildung\" name=\"d2\"/> \r\n<item label=\"3. Grad offener Hautdefekt\" name=\"d3\"/> \r\n<item label=\"4. Grad Muskulatur sichtbar\" name=\"d4\"/>\r\n<item label=\"5. Grad Knochen befallen\" name=\"d5\"/> \r\n</combobox>\r\n\r\n<label size=\"16\" label=\"Denk dran abgeheilte Wunden abzuschliessen.\" fontstyle=\"bold\"/>\r\n<checkbox name=\"pain\" label=\"Die Wunde schmerzt\"/>\r\n<label label=\"Die Schmerzintensität musst Du gesondert unter \'Schmerzeinschätzung\' eintragen.\" size=\"16\"  fontstyle=\"bold\" layout=\"p\"/>\r\n\r\n<textfield name=\"laenge\" label=\"Länge (cm)\" layout=\"br tab\" length=\"6\" hfill=\"false\"/>\r\n<textfield name=\"breite\" label=\"Breite (cm)\" layout=\"tab\" length=\"6\" hfill=\"false\"/>\r\n<textfield name=\"tiefe\" label=\"Tiefe (cm)\" layout=\"tab\" length=\"6\" hfill=\"false\"/>\r\n\r\n<tabgroup size=\"16\" fontstyle=\"bold\" label=\"Wundfläche\" name=\"tg1\">\r\n<checkbox name=\"epi\" label=\"Epithelisierung\" layout=\"left\"/>\r\n<checkbox name=\"ggran\" label=\"gute Granulation\" layout=\"left\"/>\r\n<checkbox name=\"sgran\" label=\"schlechte Granulation\" layout=\"left\"/>\r\n<checkbox name=\"fibrin\" label=\"Fibrinbelag\" layout=\"left\"/>\r\n<checkbox name=\"fnekr\" label=\"Feuchte Nekrose\" layout=\"left\"/>\r\n<checkbox name=\"tnekr\" label=\"Trockene Nekrose\" layout=\"br\"/>\r\n<checkbox name=\"inseln\" label=\"Wundheilungsinseln\" layout=\"left\"/>\r\n<checkbox name=\"hyper\" label=\"Hypergranulation\" layout=\"left\"/>\r\n</tabgroup>\r\n\r\n<tabgroup size=\"16\" fontstyle=\"bold\" label=\"Wundumgebung\" name=\"tg2\">\r\n<checkbox name=\"rosig1\" label=\"rosig\" layout=\"left\"/>\r\n<checkbox name=\"rot1\" label=\"rot\" layout=\"left\"/>\r\n<checkbox name=\"blau1\" label=\"bläulich verfärbt\" layout=\"left\"/>\r\n<checkbox name=\"marz1\" label=\"marzeriert\"  layout=\"left\"/>\r\n<checkbox name=\"verh1\" label=\"verhärtet\" layout=\"left\"/>\r\n<checkbox name=\"oedem1\" label=\"Ödembildung\" layout=\"left\"/>\r\n</tabgroup>\r\n\r\n<tabgroup size=\"16\" fontstyle=\"bold\" label=\"Wundrand\" name=\"tg3\">\r\n<checkbox name=\"rosig2\" label=\"rosig\" layout=\"left\"/>\r\n<checkbox name=\"rot2\" label=\"rot\" layout=\"left\"/>\r\n<checkbox name=\"glatt2\" label=\"glatt\" layout=\"left\"/>\r\n<checkbox name=\"geschw2\" label=\"geschwollen\"  layout=\"left\"/>\r\n<checkbox name=\"marz2\" label=\"marzeriert\" layout=\"left\"/>\r\n<checkbox name=\"nekr2\" label=\"Nekrose\" layout=\"left\"/>\r\n<checkbox name=\"taschen2\" label=\"Taschenbildung\" layout=\"br left\" enables=\"ttasche\" default=\"false\"/>\r\n<textfield name=\"ttasche\" default=\"0\" label=\"Tiefe der Tasche (cm)\" length=\"6\" hfill=\"false\" size=\"16\" fontstyle=\"bold\"  defaultdisabled=\"true\" layout=\"left\"/>\r\n</tabgroup>\r\n\r\n<tabgroup size=\"16\" fontstyle=\"bold\" label=\"Wundexsudat\" name=\"tg4\">\r\n<checkbox name=\"kein3\" label=\"kein\" layout=\"left\" />\r\n<checkbox name=\"wenig3\" label=\"wenig\" layout=\"left\"/>\r\n<checkbox name=\"viel3\" label=\"viel\" layout=\"left\"/>\r\n<checkbox name=\"klar3\" label=\"klar\" layout=\"left\"/>\r\n<checkbox name=\"blut3\" label=\"blutig\" layout=\"left\"/>\r\n<checkbox name=\"eitr3\" label=\"eitrig\" layout=\"left\"/>\r\n<checkbox name=\"gruen3\" label=\"grün\" layout=\"left\"/>\r\n<checkbox name=\"braun3\" label=\"braun\"  layout=\"left\"/>\r\n</tabgroup>\r\n\r\n<tabgroup size=\"16\" fontstyle=\"bold\" label=\"Wundgeruch (vor VW)\" name=\"tg5\">\r\n<checkbox name=\"unauf4\" label=\"unauffällig\" layout=\"left\"/>\r\n<checkbox name=\"leicht4\" label=\"leicht\" layout=\"left\"/>\r\n<checkbox name=\"uebel4\" label=\"übelriechend\" layout=\"left\"/>\r\n<checkbox name=\"suebel4\" label=\"stark übelriechend\" layout=\"left\"/>\r\n</tabgroup>\r\n\r\n<tabgroup size=\"16\" fontstyle=\"bold\" label=\"Wundgeruch (während VW)\" name=\"tg6\">\r\n<checkbox name=\"unauf5\" label=\"unauffällig\" layout=\"left\"/>\r\n<checkbox name=\"leicht5\" label=\"leicht\" layout=\"left\"/>\r\n<checkbox name=\"uebel5\" label=\"übelriechend\" layout=\"left\"/>\r\n<checkbox name=\"suebel5\" label=\"stark übelriechend\" layout=\"left\"/>\r\n</tabgroup>\r\n\r\n<tabgroup size=\"16\" fontstyle=\"bold\" label=\"Wundgeruch (nach VW)\" name=\"tg7\">\r\n<checkbox name=\"unauf6\" label=\"unauffällig\" layout=\"left\"/>\r\n<checkbox name=\"leicht6\" label=\"leicht\" layout=\"left\"/>\r\n<checkbox name=\"uebel6\" label=\"übelriechend\" layout=\"left\"/>\r\n<checkbox name=\"suebel6\" label=\"stark übelriechend\"  layout=\"left\"/>\r\n</tabgroup>','Wunde Nr.06',NULL,9,147,0,0,0)
,
