INSERT IGNORE INTO `resinfotype` (`BWINFTYP`, `XML`, `BWInfoKurz`, `BWInfoLang`, `BWIKID`, `type`, `version`, `IntervalMode`, `equiv`) VALUES('SEIZURE','<checkbox name=\"face\" label=\"Gesichtausdruck hat sich verändert\"/>\r\n\r\n<checkbox name=\"eyes\" label=\"Augen waren offen\"/>\r\n\r\n<optiongroup name=\"eyesroll\" label=\"Augen zur Seite gedreht\">\r\n<option label=\"nein\" name=\"no\"  default=\"true\" />\r\n<option label=\"links\" name=\"left\"/>\r\n<option label=\"rechts\" name=\"right\"/>\r\n</optiongroup>\r\n\r\n<checkbox name=\"handmovement\" label=\"nestelnde Bewegungen der Hände (Reiben, Streicheln, Wischen, Klopfen)\"/>\r\n\r\n<optiongroup name=\"headroll\" label=\"Kopf zur Seite gedreht\">\r\n<option label=\"nein\" name=\"no\"  default=\"true\" />\r\n<option label=\"links\" name=\"left\"/>\r\n<option label=\"rechts\" name=\"right\"/>\r\n</optiongroup>\r\n\r\n<textfield label=\"Wie häufig ?\" name=\"headroll.frequency\" innerlayout=\"left\"/>\r\n\r\n<optiongroup name=\"headroll.speed\" label=\"Geschwindidkeit der Kopfbewegung\">\r\n<option label=\"gar nicht\" name=\"noneatall\"  default=\"true\" />\r\n<option label=\"langsam\" name=\"slow\"/>\r\n<option label=\"schnell\" name=\"fast\"/>\r\n</optiongroup>\r\n\r\n<textfield label=\"folgten Zuckungen ?\" name=\"headroll.frequency\" innerlayout=\"left\" tooltip=\"Wenn ja, wie lange ?\"/>\r\n\r\n<optiongroup name=\"falling\" label=\"Wie fiel der Bewohner um ?\">\r\n<option label=\"steiff\" name=\"stiff\"  default=\"true\"  tooltip=\"wie ein Baum\" />\r\n<option label=\"sackte langsam zu Boden\" name=\"slow\"/>\r\n</optiongroup>\r\n\r\n<checkbox name=\"scream\" label=\"Schrei bzw. gepresstes, rasselndes, röchelndes Atemgeräusch\"/>\r\n\r\n<textfield label=\"Falls Lähmungen nach dem Anfall auftraten: wie lange dauerten sie an ?\" name=\"paralyze.time\" innerlayout=\"left\"/>\r\n\r\n<textfield label=\"War der Betroffene sofort ansprechbar ? Wenn nein, wie lange nicht ?\" name=\"knockout.time\" innerlayout=\"left\"/>\r\n\r\n<checkbox name=\"restless\" label=\"erneute motorische Äußerungen, bevor der Bewohner wieder ansprechbar war\"/>\r\n\r\n<textfield label=\"Falls es Nachschlaf gab, wie lange\" name=\"sleeping.time\" innerlayout=\"left\"/>\r\n\r\n<label label=\"Falls der Anfall außergewöhnlich war, beschreiben Sie bitte was anders war.\" fontstyle=\"plain\" name=\"lab2\"/>\r\n\r\n<label label=\"Vermerken Sie, ob Sie den Anfall vollständig selbst beobachtet haben bzw. welche Teile Sie und welche Mitarbeiter, Mitbewohner, Passanten usw. beobachtet haben.\" fontstyle=\"plain\" name=\"lab3\"/>','Anfall','','15','0','0','3','0');
